import React from "react";
import * as Yup from "yup";
import { Card, Col, Form } from "react-bootstrap";
import { Formik } from "formik";
import SubmitButton from "../SubmitButton";
import { useTranslation } from "react-i18next";
import { motion } from "framer-motion";

const LoginForm = ({ onSubmit, isSubmitted }) => {
  const { t } = useTranslation();

  const containerVariants = {
    hidden: { opacity: 0, x: "-100vw" },
    visible: {
      opacity: 1,
      x: 0,
      transition: { type: "spring", stiffness: 120 },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: -50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { delay: 0.2, duration: 0.5 },
    },
  };

  return (
    <Formik
      initialValues={{
        userName: "",
        password: "",
      }}
      validationSchema={Yup.object({
        userName: Yup.string()
          .min(3, t("validation:atLeast", { number: 3 }))
          .max(25, t("validation:lessThan", { number: 25 }))
          .required(t("validation:required")),
        password: Yup.string()
          .min(5, t("validation:atLeast", { number: 5 }))
          .max(15, t("validation:lessThan", { number: 15 }))
          .required(t("validation:required")),
      })}
      onSubmit={onSubmit}
    >
      {(formik) => (
        <Col sm={10} md={8} lg={6}>
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <Card className="boxShadow bg-info m-2">
              <Card.Header 
              bg="#d4edda"
              className="text-center m-2 text-dark border-rounded fs-4">
                {t("login")} ðŸ”‘
              </Card.Header>
              <motion.div
                className="m-2 p-4 bg-light border rounded-3"
                variants={itemVariants}
              >
                <Form onSubmit={formik.handleSubmit}>
                  <Form.Group className="mb-3" controlId="userName">
                    <Form.Label>{t("userName")} ðŸ‘¤</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder={t("enterUserName")}
                      name="userName"
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      value={formik.values.userName}
                      disabled={isSubmitted}
                      isValid={
                        formik.touched.userName &&
                        !formik.errors.userName &&
                        formik.values.userName
                      }
                      isInvalid={
                        (formik.touched.userName && formik.errors.userName) ||
                        formik.errors.invalidUsername
                      }
                    />
                    <Form.Control.Feedback type="invalid">
                      {formik.errors.userName}
                    </Form.Control.Feedback>
                    <Form.Control.Feedback type="invalid">
                      {formik.errors.invalidUsername}
                    </Form.Control.Feedback>
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="password">
                    <Form.Label>{t("password")} ðŸ”’</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder={t("password")}
                      name="password"
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      value={formik.values.password}
                      disabled={isSubmitted}
                      isValid={
                        formik.touched.password &&
                        !formik.errors.password &&
                        formik.values.password
                      }
                      isInvalid={
                        (formik.errors.password && formik.touched.password) ||
                        formik.errors.invalidPassword
                      }
                    />
                    <Form.Control.Feedback type="invalid">
                      {formik.errors.password}
                    </Form.Control.Feedback>
                    <Form.Control.Feedback type="invalid">
                      {formik.errors.invalidPassword}
                    </Form.Control.Feedback>
                  </Form.Group>
                  <SubmitButton
                    isSubmitted={isSubmitted}
                    isSubmitting={formik.isSubmitting}
                    name={t("login")}
                  />
                </Form>
              </motion.div>
            </Card>
          </motion.div>
        </Col>
      )}
    </Formik>
  );
};

export default LoginForm;
