import React from "react";
import { Link, NavLink } from "react-router-dom";
import { Card } from "react-bootstrap";
import { useTranslation } from "react-i18next";
import { motion } from "framer-motion";

const PostThumbnail = (props) => {
  const { t } = useTranslation();

  const cardVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: {
      opacity: 1,
      scale: 1,
      transition: { duration: 0.5 },
    },
    hover: { scale: 1.05, transition: { duration: 0.2 } },
  };

  return (
    <Link
      to={"/post/" + props.post.id}
      as={NavLink}
      className="thumbnail text-decoration-none"
    >
      <motion.div
        className="h-100"
        variants={cardVariants}
        initial="hidden"
        animate="visible"
        whileHover="hover"
      >
        <Card className="h-100 boxShadow bg-dark text-white">
          <Card.Header className="fs-5 bg-light text-dark text-center">
            {props.post.blogUser} ðŸŒŸ
          </Card.Header>
          <Card.Body>
            <Card.Title className="fs-6">{props.post.title} </Card.Title>
            <Card.Text style={{ textAlign: "justify" }}>
              {props.post.content.slice(0, 200) + " . . ."}
            </Card.Text>
          </Card.Body>
          <Card.Footer className="d-flex text-white bg-success">
            <div className="me-auto">{`${t("posted")}: ${props.post.createdOn}`}</div>
            <div>{`${t("edited")}: ${props.post.updatedOn}`}</div>
          </Card.Footer>
        </Card>
      </motion.div>
    </Link>
  );
};

export default PostThumbnail;
